import './App.css';
import { useState } from 'react';

function App() {
  var [count,setCount]=useState(0)
  function increase(){
    setCount(count++);
  }
  function decrease(){
    setCount(count--);
  }
  function reset(){
    setCount(0);
  }
  return (
    <div className="App">
      <h1>{count}</h1>
      <button onClick={increase}>+</button>
      <button onClick={decrease}>-</button>
      <button onClick={reset}>â†»</button>
    </div>
  );
}

export default App;
